:root {
  /* Light theme colors */
  --light-background: hsl(0, 0%, 100%);
  --light-foreground: hsl(15, 8%, 5%);
  --light-muted: hsl(210, 40%, 96%);
  --light-muted-foreground: hsl(215.4, 16.3%, 46.9%);
  --light-popover: hsl(0, 0%, 100%);
  --light-popover-foreground: hsl(15, 8%, 5%);
  --light-card: hsl(0, 0%, 100%);
  --light-card-foreground: hsl(15, 8%, 5%);
  --light-border: hsl(214.3, 31.8%, 91.4%);
  --light-input: hsl(214.3, 31.8%, 91.4%);
  --light-primary: hsl(207, 90%, 54%);
  --light-primary-foreground: hsl(0, 0%, 98%);
  --light-secondary: hsl(210, 40%, 96%);
  --light-secondary-foreground: hsl(15, 8%, 5%);
  --light-accent: hsl(207, 90%, 54%);
  --light-accent-foreground: hsl(0, 0%, 98%);
  --light-destructive: hsl(0, 84.2%, 60.2%);
  --light-destructive-foreground: hsl(0, 0%, 98%);
  --light-ring: hsl(207, 90%, 54%);

  /* Dark theme colors */
  --dark-background: hsl(15, 8%, 5%);
  --dark-foreground: hsl(0, 0%, 98%);
  --dark-muted: hsl(240, 3.7%, 15.9%);
  --dark-muted-foreground: hsl(240, 5%, 64.9%);
  --dark-popover: hsl(240, 10%, 3.9%);
  --dark-popover-foreground: hsl(0, 0%, 98%);
  --dark-card: hsl(240, 10%, 3.9%);
  --dark-card-foreground: hsl(0, 0%, 98%);
  --dark-border: hsl(240, 3.7%, 15.9%);
  --dark-input: hsl(240, 3.7%, 15.9%);
  --dark-primary: hsl(207, 90%, 54%);
  --dark-primary-foreground: hsl(211, 100%, 99%);
  --dark-secondary: hsl(240, 3.7%, 15.9%);
  --dark-secondary-foreground: hsl(0, 0%, 98%);
  --dark-accent: hsl(207, 90%, 54%);
  --dark-accent-foreground: hsl(0, 0%, 98%);
  --dark-destructive: hsl(0, 62.8%, 30.6%);
  --dark-destructive-foreground: hsl(0, 0%, 98%);
  --dark-ring: hsl(240, 4.9%, 83.9%);

  /* Default to light theme */
  --background: var(--light-background);
  --foreground: var(--light-foreground);
  --muted: var(--light-muted);
  --muted-foreground: var(--light-muted-foreground);
  --popover: var(--light-popover);
  --popover-foreground: var(--light-popover-foreground);
  --card: var(--light-card);
  --card-foreground: var(--light-card-foreground);
  --border: var(--light-border);
  --input: var(--light-input);
  --primary: var(--light-primary);
  --primary-foreground: var(--light-primary-foreground);
  --secondary: var(--light-secondary);
  --secondary-foreground: var(--light-secondary-foreground);
  --accent: var(--light-accent);
  --accent-foreground: var(--light-accent-foreground);
  --destructive: var(--light-destructive);
  --destructive-foreground: var(--light-destructive-foreground);
  --ring: var(--light-ring);
  --radius: 0.5rem;

  /* Custom quantum colors - adaptive */
  --quantum-blue: hsl(207, 90%, 54%);
  --quantum-green: hsl(142, 71%, 45%);
  --quantum-purple: hsl(271, 81%, 56%);
  --quantum-cyan: hsl(180, 100%, 70%);
  --quantum-pink: hsl(322, 100%, 75%);

  /* Neural network colors - adaptive */
  --neural-primary: var(--background);
  --neural-secondary: var(--secondary);
  --neural-accent: var(--primary);
  --neural-glow: hsl(142, 71%, 45%);
}

.light {
  --background: var(--light-background);
  --foreground: var(--light-foreground);
  --muted: var(--light-muted);
  --muted-foreground: var(--light-muted-foreground);
  --popover: var(--light-popover);
  --popover-foreground: var(--light-popover-foreground);
  --card: var(--light-card);
  --card-foreground: var(--light-card-foreground);
  --border: var(--light-border);
  --input: var(--light-input);
  --primary: var(--light-primary);
  --primary-foreground: var(--light-primary-foreground);
  --secondary: var(--light-secondary);
  --secondary-foreground: var(--light-secondary-foreground);
  --accent: var(--light-accent);
  --accent-foreground: var(--light-accent-foreground);
  --destructive: var(--light-destructive);
  --destructive-foreground: var(--light-destructive-foreground);
  --ring: var(--light-ring);
}

.dark {
  --background: var(--dark-background);
  --foreground: var(--dark-foreground);
  --muted: var(--dark-muted);
  --muted-foreground: var(--dark-muted-foreground);
  --popover: var(--dark-popover);
  --popover-foreground: var(--dark-popover-foreground);
  --card: var(--dark-card);
  --card-foreground: var(--dark-card-foreground);
  --border: var(--dark-border);
  --input: var(--dark-input);
  --primary: var(--dark-primary);
  --primary-foreground: var(--dark-primary-foreground);
  --secondary: var(--dark-secondary);
  --secondary-foreground: var(--dark-secondary-foreground);
  --accent: var(--dark-accent);
  --accent-foreground: var(--dark-accent-foreground);
  --destructive: var(--dark-destructive);
  --destructive-foreground: var(--dark-destructive-foreground);
  --ring: var(--dark-ring);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
    overflow-x: hidden;
  }

  html {
    /* Removed global scroll-behavior: smooth to allow fast manual scrolling */
    /* Smooth scrolling is now applied only to programmatic scrolls via JS */
  }
}

@layer components {
  /* Quantum animations */
  @keyframes quantumFloat {
    0%, 100% { transform: translateY(0px) rotate(0deg); }
    25% { transform: translateY(-20px) rotate(2deg); }
    50% { transform: translateY(-10px) rotate(-1deg); }
    75% { transform: translateY(-15px) rotate(1deg); }
  }

  @keyframes quantumPulse {
    0% { box-shadow: 0 0 0 0 hsla(207, 90%, 54%, 0.4); }
    50% { box-shadow: 0 0 0 10px hsla(207, 90%, 54%, 0.1); }
    100% { box-shadow: 0 0 0 0 hsla(207, 90%, 54%, 0); }
  }

  @keyframes neuralFlow {
    0% { transform: translateX(-100%); opacity: 0; }
    50% { opacity: 1; }
    100% { transform: translateX(100vw); opacity: 0; }
  }

  @keyframes holographicShimmer {
    0% { background-position: -200% 0; }
    100% { background-position: 200% 0; }
  }

  @keyframes holographicShimmerFast {
    0% { background-position: -200% 0; }
    100% { background-position: 200% 0; }
  }

  @keyframes matrixRain {
    0% { transform: translateY(-100vh); opacity: 1; }
    100% { transform: translateY(100vh); opacity: 0; }
  }

  @keyframes quantumGlow {
    0%, 100% { 
      text-shadow: 0 0 2px var(--quantum-blue);
      opacity: 1;
    }
    50% { 
      text-shadow: 0 0 4px var(--quantum-blue);
      opacity: 0.95;
    }
  }

  @keyframes dataStream {
    0% { 
      background-position: 0 0;
      opacity: 0.3;
    }
    50% { 
      opacity: 0.8;
    }
    100% { 
      background-position: 100px 100px;
      opacity: 0.3;
    }
  }

  @keyframes consciousnessExpand {
    0%, 100% { 
      transform: scale(1);
      opacity: 1;
    }
    50% { 
      transform: scale(1.02);
      opacity: 0.9;
    }
  }

  @keyframes realityBend {
    0%, 100% { 
      transform: translateY(0px);
      filter: brightness(1);
    }
    50% { 
      transform: translateY(-2px);
      filter: brightness(1.05);
    }
  }

  .quantum-float {
    animation: quantumFloat 6s ease-in-out infinite;
  }

  .perspective-1000 {
    perspective: 1000px;
    transform-style: preserve-3d;
  }

  .quantum-pulse {
    animation: quantumPulse 2s infinite;
  }

  .neural-flow {
    animation: neuralFlow 3s linear infinite;
  }

  .holographic-shimmer {
    background: linear-gradient(
      90deg,
      transparent,
      var(--quantum-blue),
      var(--quantum-green),
      var(--quantum-purple),
      transparent
    );
    background-size: 200% 100%;
    animation: holographicShimmerFast 3s ease-in-out infinite;
  }

  .quantum-glow {
    animation: quantumGlow 8s ease-in-out infinite;
  }

  .data-stream {
    background-image: 
      linear-gradient(45deg, transparent 25%, var(--quantum-blue) 25%, var(--quantum-blue) 50%, transparent 50%),
      linear-gradient(-45deg, transparent 25%, var(--quantum-green) 25%, var(--quantum-green) 50%, transparent 50%);
    background-size: 30px 30px;
    animation: dataStream 4s linear infinite;
  }

  .consciousness-expand {
    animation: consciousnessExpand 4s ease-in-out infinite;
  }

  .reality-bend {
    animation: realityBend 3s ease-in-out infinite;
  }

  /* Unified scroll animation system - DISABLED (animations removed, content always visible) */
  .scroll-animate {
    opacity: 1;
    transform: translate3d(0, 0, 0);
  }

  .scroll-animate.animate-in {
    opacity: 1;
    transform: translate3d(0, 0, 0);
  }

  @media (max-width: 768px) {
    .scroll-animate {
      opacity: 1;
      transform: translate3d(0, 0, 0);
    }

    /* Smoother scroll behavior on mobile */
    html {
      scroll-behavior: smooth;
      -webkit-overflow-scrolling: touch;
    }

    /* Reduce motion for mobile performance */
    .consciousness-expand {
      animation-duration: 6s;
    }

    .reality-bend {
      animation-duration: 4s;
    }

    .quantum-float {
      animation-duration: 8s;
    }
  }

  /* Hardware acceleration for all animated elements */
  .quantum-card,
  .gradient-text,
  .consciousness-expand,
  .reality-bend,
  .quantum-float,
  .scroll-animate {
    -webkit-transform: translateZ(0);
    transform: translateZ(0);
    -webkit-backface-visibility: hidden;
    backface-visibility: hidden;
    -webkit-perspective: 1000;
    perspective: 1000;
  }

  /* Gradient text effects */
  .gradient-text {
    background: linear-gradient(
      45deg,
      var(--quantum-blue),
      var(--quantum-green),
      var(--quantum-purple),
      var(--quantum-cyan)
    );
    background-size: 300% 300%;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: holographicShimmer 35s ease-in-out infinite;
  }

  /* Slower animation specifically for name/important headings */
  .gradient-text-slow {
    background: linear-gradient(
      45deg,
      var(--quantum-blue),
      var(--quantum-green),
      var(--quantum-purple),
      var(--quantum-cyan)
    );
    background-size: 400% 400%;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: holographicShimmer 45s ease-in-out infinite;
  }

  /* Elegant hero name styling with character animations */
  .hero-name {
    background: linear-gradient(
      135deg,
      var(--foreground) 0%,
      var(--quantum-blue) 30%,
      var(--foreground) 60%,
      var(--quantum-purple) 100%
    );
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    text-shadow: 0 0 30px rgba(59, 130, 246, 0.3);
    letter-spacing: -0.02em;
  }

  /* Enhanced hero name styling with modern typography */
  .hero-name-enhanced {
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
    font-variation-settings: 'wght' 900, 'slnt' 0;
    letter-spacing: -0.04em;
    line-height: 0.85;
    text-rendering: optimizeLegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    background: linear-gradient(
      135deg,
      var(--foreground) 0%,
      var(--quantum-blue) 25%,
      var(--quantum-purple) 50%,
      var(--quantum-cyan) 75%,
      var(--foreground) 100%
    );
    background-size: 300% 300%;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: holographicShimmer 20s ease-in-out infinite;
    text-shadow: 
      0 0 40px rgba(59, 130, 246, 0.4),
      0 0 80px rgba(139, 92, 246, 0.2);
    position: relative;
  }

  .hero-name-enhanced::before {
    content: '';
    position: absolute;
    inset: 0;
    background: inherit;
    background-clip: inherit;
    -webkit-background-clip: inherit;
    -webkit-text-fill-color: inherit;
    filter: blur(2px);
    opacity: 0.3;
    z-index: -1;
  }

  .hero-name .char {
    display: inline-block;
    opacity: 0;
    transform: translateY(50px) rotateX(90deg);
    animation: charReveal 0.8s ease-out forwards;
  }

  @keyframes charReveal {
    to {
      opacity: 1;
      transform: translateY(0) rotateX(0);
    }
  }

  /* Elegant navigation name styling */
  .nav-name {
    color: var(--foreground);
    text-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);
    font-weight: 700;
    letter-spacing: -0.01em;
  }

  .dark .nav-name {
    text-shadow: 0 1px 2px rgba(0, 0, 0, 0.3);
  }

  /* Card hover effects - theme adaptive */
  .quantum-card {
    background: var(--card);
    backdrop-filter: blur(20px);
    border: 1px solid var(--border);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .light .quantum-card {
    background: linear-gradient(145deg, 
      hsla(0, 0%, 100%, 0.9), 
      hsla(210, 40%, 96%, 0.8)
    );
    box-shadow: 
      0 8px 32px rgba(0, 0, 0, 0.08),
      0 0 0 1px rgba(59, 130, 246, 0.1);
  }

  .dark .quantum-card {
    background: linear-gradient(145deg, 
      hsla(240, 10%, 3.9%, 0.8), 
      hsla(240, 3.7%, 15.9%, 0.6)
    );
    box-shadow: 
      0 8px 32px rgba(0, 0, 0, 0.12),
      0 0 0 1px rgba(255, 255, 255, 0.05);
  }

  .quantum-card:hover {
    transform: translateY(-10px) scale(1.02);
    border-color: var(--quantum-blue);
  }

  .light .quantum-card:hover {
    box-shadow: 
      0 25px 50px -12px rgba(0, 0, 0, 0.15),
      0 0 50px rgba(59, 130, 246, 0.2),
      inset 0 1px 0 rgba(255, 255, 255, 0.8);
  }

  .dark .quantum-card:hover {
    box-shadow: 
      0 25px 50px -12px rgba(0, 0, 0, 0.5),
      0 0 50px rgba(59, 130, 246, 0.3),
      inset 0 1px 0 rgba(255, 255, 255, 0.1);
  }

  /* Particle system */
  .particle {
    position: absolute;
    background: var(--quantum-blue);
    border-radius: 50%;
    pointer-events: none;
    animation: particleFloat 6s linear infinite;
  }

  @keyframes particleFloat {
    0% {
      transform: translateY(100vh) scale(0);
      opacity: 0;
    }
    10% {
      opacity: 1;
    }
    90% {
      opacity: 1;
    }
    100% {
      transform: translateY(-100vh) scale(1);
      opacity: 0;
    }
  }

  /* Neural network connections */
  .neural-connection {
    position: absolute;
    height: 1px;
    background: linear-gradient(90deg, 
      transparent, 
      var(--quantum-blue), 
      transparent
    );
    animation: neuralFlow 4s linear infinite;
  }

  /* Quantum background dots */
  .quantum-dots {
    background-image: radial-gradient(
      circle,
      hsla(207, 90%, 54%, 0.3) 1px,
      transparent 1px
    );
    background-size: 50px 50px;
    animation: dataStream 20s linear infinite;
  }

  /* Holographic glass effect */
  .holo-glass {
    background: linear-gradient(145deg,
      hsla(207, 90%, 54%, 0.1),
      hsla(142, 71%, 45%, 0.1)
    );
    backdrop-filter: blur(10px);
    border: 1px solid hsla(255, 255%, 255%, 0.1);
  }

  /* Skill progress animation */
  .skill-progress {
    position: relative;
    overflow: hidden;
  }

  .skill-progress::after {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg,
      transparent,
      hsla(255, 255%, 255%, 0.3),
      transparent
    );
    animation: skillShimmer 2s ease-in-out infinite;
  }

  @keyframes skillShimmer {
    0% { left: -100%; }
    100% { left: 100%; }
  }

  /* Cursor effects */
  .quantum-cursor {
    pointer-events: none;
    position: fixed;
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background: var(--quantum-blue);
    mix-blend-mode: difference;
    z-index: 9999;
    transition: transform 0.1s ease;
  }

  /* Responsive quantum effects */
  @media (max-width: 768px) {
    .quantum-float {
      animation-duration: 4s;
    }

    .consciousness-expand {
      animation-duration: 6s;
    }

    .reality-bend {
      animation: none;
    }
  }

  /* Performance optimizations */
  .will-change-transform {
    will-change: transform;
  }

  .will-change-opacity {
    will-change: opacity;
  }

  .gpu-accelerated {
    transform: translateZ(0);
    backface-visibility: hidden;
    perspective: 1000;
  }

  /* Masonry and Cascading Effects */
  @keyframes cascadeIn {
    0% { 
      transform: translateY(60px) scale(0.8);
      opacity: 0;
    }
    100% { 
      transform: translateY(0) scale(1);
      opacity: 1;
    }
  }

  @keyframes videoFrameTransition {
    0% { 
      transform: scale(1) rotate(0deg);
      filter: brightness(1);
    }
    50% { 
      transform: scale(1.02) rotate(0.5deg);
      filter: brightness(1.1);
    }
    100% { 
      transform: scale(1) rotate(0deg);
      filter: brightness(1);
    }
  }

  @keyframes masonryFloat {
    0%, 100% { 
      transform: translateY(0px);
    }
    50% { 
      transform: translateY(-5px);
    }
  }

  @keyframes dataFlow {
    0% { 
      transform: translateX(-100px);
      opacity: 0;
    }
    50% { 
      opacity: 1;
    }
    100% { 
      transform: translateX(100vw);
      opacity: 0;
    }
  }

  @keyframes parallaxSlide {
    0% { 
      transform: translateX(-100%);
    }
    100% { 
      transform: translateX(100%);
    }
  }

  /* Text Highlighting Animations */
  @keyframes highlight {
    0% {
      background: transparent;
      color: inherit;
      font-weight: inherit;
      transform: scale(1);
    }
    25% {
      background: linear-gradient(90deg, rgba(59, 130, 246, 0.3) 0%, rgba(139, 92, 246, 0.3) 100%);
      color: rgb(59, 130, 246);
      font-weight: 600;
      transform: scale(1.02);
    }
    75% {
      background: linear-gradient(90deg, rgba(59, 130, 246, 0.3) 0%, rgba(139, 92, 246, 0.3) 100%);
      color: rgb(59, 130, 246);
      font-weight: 600;
      transform: scale(1.02);
    }
    100% {
      background: transparent;
      color: inherit;
      font-weight: 500;
      transform: scale(1);
    }
  }

  @keyframes highlightTitle {
    0% {
      transform: translateY(10px);
      opacity: 0.7;
      text-shadow: none;
      font-weight: inherit;
    }
    50% {
      transform: translateY(0);
      opacity: 1;
      text-shadow: 0 0 20px rgba(59, 130, 246, 0.5);
      font-weight: 800;
    }
    100% {
      transform: translateY(0);
      opacity: 1;
      text-shadow: none;
      font-weight: 700;
    }
  }

  @keyframes highlightText {
    0% {
      opacity: 0.7;
      transform: translateX(-5px);
      font-weight: inherit;
    }
    50% {
      opacity: 1;
      transform: translateX(0);
      font-weight: 600;
    }
    100% {
      opacity: 1;
      transform: translateX(0);
      font-weight: 500;
    }
  }

  .animate-highlight {
    animation: highlight 3s ease-in-out;
    animation-fill-mode: forwards;
  }

  .animate-highlight-title {
    animation: highlightTitle 2s ease-out;
    animation-fill-mode: forwards;
  }

  .animate-highlight-text {
    animation: highlightText 1.5s ease-out;
    animation-fill-mode: forwards;
  }

  .cascade-in {
    animation: cascadeIn 0.8s cubic-bezier(0.4, 0, 0.2, 1) forwards;
  }

  .video-frame-transition {
    animation: videoFrameTransition 2s ease-in-out infinite;
  }

  .masonry-float {
    animation: masonryFloat 3s ease-in-out infinite;
  }

  .data-flow {
    animation: dataFlow 5s linear infinite;
  }

  .parallax-slide {
    animation: parallaxSlide 8s linear infinite;
  }

  /* Scroll-triggered animations */
  .scroll-reveal {
    opacity: 0;
    transform: translateY(30px);
    transition: all 0.6s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .scroll-reveal.revealed {
    opacity: 1;
    transform: translateY(0);
  }

  /* Line clamp utility */
  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  /* Masonry grid improvements */
  .masonry-grid {
    column-count: 1;
    column-gap: 1.5rem;
  }

  @media (min-width: 640px) {
    .masonry-grid {
      column-count: 2;
    }
  }

  @media (min-width: 1024px) {
    .masonry-grid {
      column-count: 3;
    }
  }

  @media (min-width: 1280px) {
    .masonry-grid {
      column-count: 4;
    }
  }

  .masonry-item {
    break-inside: avoid;
    margin-bottom: 1.5rem;
    display: inline-block;
    width: 100%;
  }

  /* Video-like scroll effects */
  .scroll-video-frame {
    position: sticky;
    top: 0;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  /* Parallax timeline effects */
  .timeline-parallax {
    transform-style: preserve-3d;
  }

  .timeline-item {
    transform: translateZ(0);
    will-change: transform, opacity;
  }

  /* Enhanced quantum effects for new components */
  .quantum-masonry-card {
    background: linear-gradient(145deg, 
      hsla(240, 10%, 3.9%, 0.9), 
      hsla(240, 3.7%, 15.9%, 0.7)
    );
    backdrop-filter: blur(15px);
    border: 1px solid hsla(207, 90%, 54%, 0.15);
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .quantum-masonry-card:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 
      0 20px 40px -12px hsla(0, 0%, 0%, 0.4),
      0 0 40px hsla(207, 90%, 54%, 0.2),
      inset 0 1px 0 hsla(255, 255%, 255%, 0.1);
    border-color: hsla(207, 90%, 54%, 0.3);
  }
}

