// Mobile detection utility
const isMobile = () => {
  return /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent) ||
         (window.innerWidth <= 768);
};

export default function MasonryShowcase() {
  const containerRef = useRef<HTMLDivElement>(null);
  const [scrollProgress, setScrollProgress] = useState(0);
  const [visibleItems, setVisibleItems] = useState<Set<string>>(new Set());

  useEffect(() => {
    const handleScroll = () => {
      if (!containerRef.current) return;

      const rect = containerRef.current.getBoundingClientRect();
      const windowHeight = window.innerHeight;

      const progress = Math.max(0, Math.min(1, (windowHeight - rect.top) / (windowHeight + rect.height)));
      setScrollProgress(progress);

      // Observe individual masonry items for staggered animation
      const items = containerRef.current.querySelectorAll('[data-masonry-item]');
      const newVisibleItems = new Set<string>();

      items.forEach(item => {
        const itemRect = item.getBoundingClientRect();
        if (itemRect.top < windowHeight && itemRect.bottom > 0) {
          const itemId = item.getAttribute('data-masonry-item');
          if (itemId) newVisibleItems.add(itemId);
        }
      });

      setVisibleItems(newVisibleItems);
    };

    const throttledScroll = () => {
      requestAnimationFrame(handleScroll);
    };

    window.addEventListener('scroll', throttledScroll);
    handleScroll();

    return () => window.removeEventListener('scroll', throttledScroll);
  }, []);

  const getSizeClasses = (size: string) => {
    switch (size) {
      case 'small':
        return 'col-span-1 row-span-1 h-56 sm:h-64';
      case 'medium':
        return 'col-span-1 row-span-2 h-72 sm:h-80';
      case 'large':
        return 'col-span-2 row-span-2 h-80 sm:h-96';
      default:
        return 'col-span-1 row-span-1 h-56 sm:h-64';
    }
  };

  const renderMedia = (item: MasonryItem) => {
    const Icon = item.icon;

    switch (item.media.type) {
      case 'video':
        return (
          <div className="relative w-full h-full flex items-center justify-center">
            <div className="absolute inset-0 bg-black/40 backdrop-blur-sm" />
            <Button
              variant="ghost"
              size="icon"
              className="relative z-10 w-16 h-16 rounded-full bg-white/20 hover:bg-white/30 transition-all duration-300"
            >
              <Play className="w-8 h-8 text-white" />
            </Button>
          </div>
        );

      case 'chart':
        return (
          <div className="relative w-full h-full flex items-center justify-center overflow-hidden">
            {/* Smooth chart visualization */}
            <div className="absolute inset-0">
              {[...Array(8)].map((_, i) => (
                <div
                  key={i}
                  className="absolute bottom-4 bg-white/30 rounded-t transition-all duration-2000"
                  style={{
                    left: `${10 + i * 10}%`,
                    width: '8%',
                    height: `${25 + Math.sin(scrollProgress * Math.PI + i * 0.5) * 20}%`
                  }}
                />
              ))}
            </div>
            <Icon className="w-16 h-16 text-white/80 relative z-10" />
          </div>
        );

      default:
        return (
          <div className="relative w-full h-full flex items-center justify-center">
            <Icon className="w-16 h-16 text-white/80" />
            {/* Static decorative elements */}
            <div className="absolute inset-0">
              {[...Array(4)].map((_, i) => (
                <div
                  key={i}
                  className="absolute w-1 h-1 bg-white/30 rounded-full"
                  style={{
                    left: `${20 + i * 20}%`,
                    top: `${30 + i * 15}%`
                  }}
                />
              ))}
            </div>
          </div>
        );
    }
  };

  return (
    <section 
      id="showcase"
      ref={containerRef}
      className="py-12 relative overflow-hidden"
    >
      {/* Minimal background effect */}
      <div className="absolute inset-0 opacity-5">
        <div className="absolute top-10 left-10 w-2 h-2 bg-primary/50 rounded-full"></div>
        <div className="absolute top-32 right-20 w-1 h-1 bg-accent/50 rounded-full"></div>
        <div className="absolute bottom-20 left-1/3 w-1 h-1 bg-primary/50 rounded-full"></div>
        <div className="absolute bottom-40 right-1/4 w-2 h-2 bg-accent/50 rounded-full"></div>
      </div>

      {/* Subtle static grid overlay */}
      <div className="absolute inset-0 opacity-5">
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            className="absolute w-px h-16 bg-gradient-to-b from-primary/50 to-transparent"
            style={{
              left: `${(i * 8) % 100}%`,
              top: `${(i * 12) % 100}%`,
              opacity: 0.2
            }}
          />
        ))}
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
        <div className="text-center mb-8 sm:mb-16">
          <h2 className="text-4xl md:text-5xl font-bold mb-4 gradient-text scroll-animate">
            Interactive Portfolio Showcase
          </h2>
          <div className="w-24 h-1 bg-gradient-to-r from-primary to-accent mx-auto mb-6 scroll-animate" />
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto scroll-animate">
            Explore my work, projects, achievements, and innovations
          </p>
        </div>

        {/* Masonry Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-2 sm:gap-6 auto-rows-max">
          {masonryItems.map((item, index) => {
            const isVisible = visibleItems.has(item.id);
            const itemProgress = isVisible ? 1 : 0;

            return (
              <div
                key={item.id}
                data-masonry-item={item.id}
                className={`${getSizeClasses(item.size)} hover:scale-105`}
                style={{
                  opacity: isVisible ? 1 : 0.3,
                  transform: `translateY(${isVisible ? 0 : 30}px) scale(${isVisible ? 1 : 0.95})`,
                  transition: 'all 700ms ease-in-out',
                  transitionDelay: `${index * 100}ms`
                }}
              >
                <div className="quantum-card h-full rounded-xl overflow-hidden shadow-lg group cursor-pointer hover:scale-105 transition-all duration-500">
                  {/* Quantum hover overlay */}
                  <div className="absolute inset-0 bg-gradient-to-br from-primary/5 via-transparent to-accent/5 opacity-0 group-hover:opacity-100 transition-opacity duration-500 z-10 pointer-events-none"></div>

                  {/* Media Section */}
                  <div className={`relative h-2/5 sm:h-2/3 bg-gradient-to-br ${item.media.gradient} overflow-hidden`}>
                    {renderMedia(item)}

                    {/* Enhanced hover overlay */}
                    <div className="absolute inset-0 bg-black/0 group-hover:bg-black/20 transition-all duration-300" />

                    {/* Type badge */}
                    <div className="absolute top-4 right-4">
                      <span className="px-3 py-1 bg-black/40 backdrop-blur-sm rounded-full text-white text-xs font-medium capitalize">
                        {item.type}
                      </span>
                    </div>

                    {/* Size indicator */}
                    <div className="absolute top-4 left-4">
                      <div className={`w-3 h-3 rounded-full ${
                        item.size === 'large' ? 'bg-green-400' :
                        item.size === 'medium' ? 'bg-yellow-400' : 'bg-blue-400'
                      }`} />
                    </div>
                  </div>

                  {/* Content Section */}
                  <div className="h-3/5 sm:h-1/3 p-2 sm:p-4 flex flex-col justify-start space-y-1 overflow-hidden">
                    {/* Title and description */}
                    <div className="flex-shrink-0">
                      <h3 className="font-bold text-sm sm:text-lg mb-1 line-clamp-1 group-hover:text-primary transition-colors duration-300">
                        {item.title}
                      </h3>
                      <p className="text-muted-foreground text-xs sm:text-sm mb-1 sm:mb-2 line-clamp-1 sm:line-clamp-2 group-hover:text-foreground transition-colors duration-300">
                        {item.description}
                      </p>
                    </div>

                    {/* Metrics with quantum effects */}
                    {item.metrics && (
                      <div className="flex justify-center space-x-2 sm:space-x-3 mb-1 sm:mb-2 flex-shrink-0">
                        {item.metrics.map((metric, metricIndex) => (
                          <div key={metricIndex} className="text-center group-hover:scale-105 transition-transform duration-300 flex-1">
                            <div className="text-primary font-bold text-xs sm:text-sm group-hover:text-accent transition-colors duration-300">{metric.value}</div>
                            <div className="text-muted-foreground text-[8px] sm:text-xs leading-tight">{metric.label}</div>
                          </div>
                        ))}
                      </div>
                    )}

                    {/* Tags with quantum effects */}
                    <div className="flex flex-wrap gap-0.5 sm:gap-1 mb-1 sm:mb-2 flex-shrink-0">
                      {item.tags.slice(0, 3).map((tag, tagIndex) => (
                        <span
                          key={tagIndex}
                          className="px-1 sm:px-1.5 py-0.5 bg-secondary/80 hover:bg-secondary text-foreground text-[8px] sm:text-xs rounded transition-all duration-300 hover:scale-105 cursor-pointer"
                          style={{ animationDelay: `${tagIndex * 50}ms` }}
                        >
                          {tag}
                        </span>
                      ))}
                      {item.tags.length > 3 && (
                        <span className="px-1 sm:px-1.5 py-0.5 bg-secondary/80 text-muted-foreground text-[8px] sm:text-xs rounded transition-all duration-300 hover:scale-105">
                          +{item.tags.length - 3}
                        </span>
                      )}
                    </div>

                    {/* Action buttons - Hidden on mobile to save space */}
                    <div className="hidden sm:flex space-x-2 opacity-0 group-hover:opacity-100 transition-all duration-300 transform translate-y-1 group-hover:translate-y-0 mt-auto flex-shrink-0">
                      <Button variant="outline" size="sm" className="flex-1 text-xs px-2 py-1 h-8 hover:bg-primary/10 hover:border-primary/50 transition-all duration-300 flex items-center justify-center">
                        <span>View</span>
                      </Button>
                      {item.type === 'project' && (
                        <Button variant="outline" size="sm" className="text-xs px-2 py-1 h-8 hover:bg-accent/10 hover:border-accent/50 transition-all duration-300 flex items-center justify-center">
                          <Github className="w-3 h-3" />
                        </Button>
                      )}
                    </div>
                  </div>

                  {/* Progress indicator */}
                  <div className="absolute bottom-0 left-0 right-0 h-1 bg-secondary">
                    <div 
                      className="h-full bg-gradient-to-r from-primary to-accent transition-all duration-1000"
                      style={{ width: `${itemProgress * 100}%` }}
                    />
                  </div>

                  {/* Subtle border glow on hover */}
                  <div className="absolute inset-0 rounded-xl border-2 border-transparent group-hover:border-primary/20 transition-all duration-500"></div>
                </div>
              </div>
            );
          })}
        </div>

        {/* Floating stats with quantum effects */}
        <div className="mt-8 sm:mt-16 grid grid-cols-2 md:grid-cols-4 gap-3 sm:gap-6">
          {[
            { label: 'Projects', value: '50+', color: 'text-blue-400' },
            { label: 'Technologies', value: '25+', color: 'text-green-400' },
            { label: 'Achievements', value: '15+', color: 'text-purple-400' },
            { label: 'Demos', value: '8+', color: 'text-orange-400' }
          ].map((stat, index) => (
            <div
              key={stat.label}
              className="text-center p-4 quantum-card rounded-lg transform transition-all duration-1000 hover:scale-105 group cursor-pointer"
              style={{
                transitionDelay: `${index * 200}ms`,
                transform: `translateY(${scrollProgress > 0.5 ? 0 : 20}px)`,
                opacity: scrollProgress > 0.5 ? 1 : 0.6
              }}
            >
              {/* Hover effect overlay */}
              <div className="absolute inset-0 bg-gradient-to-br from-primary/5 via-transparent to-accent/5 opacity-0 group-hover:opacity-100 transition-opacity duration-500 rounded-lg"></div>

              <div className="relative z-10">
                <div className={`text-3xl font-bold ${stat.color} mb-2`}>{stat.value}</div>
                <div className="text-muted-foreground group-hover:text-foreground transition-colors duration-300">{stat.label}</div>
              </div>

              {/* Subtle border glow on hover */}
              <div className="absolute inset-0 rounded-lg border-2 border-transparent group-hover:border-primary/20 transition-all duration-500"></div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

