                      }`}
                    >
                      <img
                        src={stJosephsLogo}
                        alt="St Joseph's Public School Logo"
                        className="w-8 h-8 md:w-12 md:h-12 object-contain"
                      />
                    </div>
                  ) : event.description.includes("Anahata") ||
                    event.title.includes("Anahata") ? (
                    /* Larger node for Anahata Art and Design logo */
                    <div
                      className={`w-16 h-16 md:w-20 md:h-20 rounded-full bg-white flex items-center justify-center border-4 border-background transition-all duration-500 shadow-lg overflow-hidden ${
                        isActive ? "scale-110 shadow-2xl" : "scale-100"
                      }`}
                    >
                      <img
                        src={stonedSantaLogo}
                        alt="Anahata Art and Design Logo"
                        className="w-8 h-8 md:w-12 md:h-12 object-contain"
                      />
                    </div>
                  ) : (
                    /* Regular sized node for other entries */
                    <div
                      className={`w-12 h-12 md:w-16 md:h-16 rounded-full bg-gradient-to-r ${event.color} flex items-center justify-center border-4 border-background transition-all duration-500 ${
                        isActive
                          ? "scale-110 shadow-lg shadow-primary/30"
                          : "scale-100"
                      }`}
                    >
                      <Icon className="w-6 h-6 md:w-8 md:h-8 text-white" />
                    </div>
                  )}
                </div>

                {/* Container for both Content Card and Workplace Gallery */}
                <div
                  className={`flex flex-col lg:flex-row lg:items-start gap-6 lg:gap-8 ${
                    index % 2 === 0 ? "lg:flex-row" : "lg:flex-row-reverse"
                  } max-w-6xl mx-auto px-4 pt-8 md:pt-4`}
                >
                  {/* Content Card */}
                  <div className="w-full lg:flex-1 lg:max-w-2xl">
                    <div
                      className="quantum-card p-6 rounded-xl shadow-lg overflow-hidden"
                      style={{
                        opacity: shouldAnimate ? 1 : 0.4,
                        transform: `translate3d(0, ${shouldAnimate ? 0 : 30}px, 0)`,
                        transition:
                          "all 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94)",
                        willChange: shouldAnimate
                          ? "auto"
                          : "transform, opacity",
                      }}
                    >
                      {/* Company Logo Header */}
                      {event.companyImage && (
                        <div className="relative -m-6 mb-6">
                          <div
                            className="h-48 bg-cover bg-center relative overflow-hidden"
                            style={{
                              backgroundImage: `url(${event.companyImage})`,
                              transform: `translate3d(0, ${shouldAnimate ? 0 : 20}px, 0) scale(${shouldAnimate ? 1 : 0.95})`,
                              opacity: shouldAnimate ? 1 : 0.7,
                              transition:
                                "all 0.7s cubic-bezier(0.25, 0.46, 0.45, 0.94)",
                              willChange: shouldAnimate
                                ? "transform, opacity"
                                : "auto",
                            }}
                          >
                            {/* Overlay for text readability */}
                            <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-black/20 to-transparent" />

                            {/* Parallax Effect Elements */}
                            <div
                              className="absolute top-4 right-4 w-16 h-16 rounded-full bg-white/10 backdrop-blur-sm border border-white/20"
                              style={{
                                transform: `translateY(${scrollProgress * -30}px) rotate(${scrollProgress * 180}deg)`,
                                opacity: 0.6,
                              }}
                            />
                          </div>
                        </div>
                      )}

                      <h3 className="text-2xl font-bold mb-3">{event.title}</h3>

                      {/* Year and Date Range Pills */}
                      <div className="flex flex-wrap gap-2 mb-3">
                        <span
                          className={`inline-block px-3 py-1 rounded-full text-sm font-bold bg-gradient-to-r ${event.color} text-white shadow-sm`}
                        >
                          {event.year}
                        </span>
                        <span
                          className={`inline-block px-3 py-1 rounded-full text-xs font-medium border ${isActive ? "border-primary/50 bg-primary/10 text-primary" : "border-border bg-secondary/50 text-muted-foreground"} transition-all duration-300`}
                        >
                          ð {event.dateRange}
                        </span>
                      </div>

                      <p className="text-muted-foreground mb-4 leading-relaxed">
                        {event.description}
                      </p>

                      {/* Achievements with Clean Icons */}
                      <div className="space-y-3">
                        {event.achievements.map(
                          (achievement, achievementIndex) => {
                            const getIcon = () => {
                              if (achievementIndex === 0) return Target;
                              if (achievementIndex === 1) return Zap;
                              return Settings;
                            };

                            const IconComponent = getIcon();
                            const isTechStack = achievementIndex === 2;

                            return (
                              <div
                                key={achievementIndex}
                                className="flex items-start space-x-3"
                              >
                                <div
                                  className={`flex-shrink-0 w-5 h-5 rounded-full bg-gradient-to-r ${event.color} flex items-center justify-center mt-0.5 shadow-sm`}
                                >
                                  <IconComponent className="w-3 h-3 text-white" />
                                </div>
                                <div className="flex-1">
                                  <p
                                    className={`${
                                      isTechStack
                                        ? "text-xs text-muted-foreground leading-relaxed"
                                        : "text-sm text-foreground leading-relaxed"
                                    }`}
                                  >
                                    {isTechStack && (
                                      <span className="inline-flex items-center px-2 py-1 bg-primary/15 text-primary text-xs font-medium rounded mr-2 mb-1">
                                        <Settings className="w-3 h-3 mr-1" />
                                        Tech Stack
                                      </span>
                                    )}
                                    <span
                                      dangerouslySetInnerHTML={{
                                        __html: isTechStack
                                          ? achievement.replace(
                                              "Technologies: ",
                                              "",
                                            )
                                          : achievement,
                                      }}
                                    />
                                  </p>
                                </div>
                              </div>
                            );
                          },
                        )}
                      </div>

                      {/* Progress Bar */}
                      <div className="mt-4 h-1 bg-secondary rounded-full overflow-hidden">
                        <div
                          className={`h-full bg-gradient-to-r ${event.color} transition-all duration-1000`}
                          style={{ width: `${itemProgress * 100}%` }}
                        />
                      </div>
                    </div>
                  </div>

                  {/* Workplace Gallery - Separate Container */}
                  <div className="w-full lg:flex-1 lg:max-w-sm">
                    {/* Debug: {JSON.stringify(event.workplaceImages)} */}
                    {event.workplaceImages &&
                    event.workplaceImages.length > 0 ? (
                      <div className="bg-background/95 backdrop-blur-sm border border-border rounded-xl overflow-hidden shadow-xl">
                        <div className="p-4 pb-2">
                          <h4 className="text-sm font-semibold text-muted-foreground mb-3 flex items-center">
                            <Target className="w-4 h-4 mr-2" />
                            Workplace Gallery
                          </h4>
                        </div>

                        {/* Main Image Display with Touch Support */}
                        <div
                          className="relative h-64 overflow-hidden"
                          onTouchStart={handleTouchStart}
                          onTouchMove={handleTouchMove}
                          onTouchEnd={() =>
                            handleTouchEnd(
                              index,
                              event.workplaceImages?.length || 0,
                            )
                          }
                        >
                          {event.workplaceImages
                            ?.slice(0, 5)
                            .map((image, imageIndex) => {
                              // Use manual selection if available, otherwise use scroll-based selection
                              const manualIndex = manualImageIndices[index];
                              const imageCount =
                                event.workplaceImages?.length || 1;

                              let currentImageIndex;
                              if (manualIndex !== undefined) {
                                currentImageIndex = manualIndex;
                              } else if (isAutoScrolling) {
                                // During auto-scroll, always show the first image (index 0)
                                // This gives you control over what visitors see during automated journey
                                currentImageIndex = 0;
                              } else {
                                // Mobile-optimized scroll-based cycling with reduced sensitivity
                                const scrollMultiplier =
                                  imageCount > 1 ? imageCount * 1.5 : 1;
                                const baseProgress = scrollProgress * 8; // Reduced sensitivity for smoother mobile
                                const offsetProgress = baseProgress + index * 2; // Less aggressive timing per event
                                const cycleProgress =
                                  offsetProgress % (imageCount * 3); // Much slower cycling
                                currentImageIndex =
                                  Math.floor(cycleProgress / 3) % imageCount;
                              }

                              const isCurrentImage =
                                imageIndex === currentImageIndex;

                              return (
                                <div
                                  key={imageIndex}
                                  className={`absolute inset-0 transition-all duration-800 ease-out ${
                                    isCurrentImage
                                      ? "opacity-100 scale-100"
                                      : "opacity-0 scale-105"
                                  }`}
                                  style={{
                                    transform: `translate3d(${isCurrentImage ? 0 : imageIndex < currentImageIndex ? -100 : 100}px, 0, 0) scale(${isCurrentImage ? 1 : 0.95})`,
                                    zIndex: isCurrentImage ? 10 : 1,
                                    willChange: isCurrentImage
                                      ? "transform"
                                      : "auto",
                                  }}
                                >
                                  <img
                                    src={image}
                                    alt={`${event.title} workplace ${imageIndex + 1}`}
                                    className="w-full h-full object-contain object-center bg-gradient-to-br from-secondary/20 to-muted/20"
                                  />

                                  {/* Image overlay with parallax effect */}
                                  <div className="absolute inset-0 bg-gradient-to-t from-black/20 via-transparent to-transparent" />

                                  {/* Image counter */}
                                  <div className="absolute bottom-4 right-4 bg-black/80 text-white text-sm px-3 py-1 rounded-full backdrop-blur-sm">
                                    {currentImageIndex + 1} of{" "}
                                    {event.workplaceImages?.length || 0}
                                  </div>

                                  {/* Clickable Progress indicator for current image */}
                                  {isCurrentImage && (
                                    <div className="absolute bottom-4 left-4">
                                      <div className="flex space-x-1">
                                        {event.workplaceImages
                                          ?.slice(0, 5)
                                          .map((_, dotIndex) => (
                                            <button
                                              key={dotIndex}
                                              onClick={() =>
                                                handleImageClick(
                                                  index,
                                                  dotIndex,
                                                )
                                              }
                                              className={`w-3 h-3 rounded-full transition-all duration-300 hover:scale-125 cursor-pointer ${
                                                dotIndex === currentImageIndex
                                                  ? "bg-white shadow-lg"
                                                  : "bg-white/40 hover:bg-white/60"
                                              }`}
                                              aria-label={`View image ${dotIndex + 1} of ${event.workplaceImages?.length}`}
                                            />
                                          ))}
                                      </div>
                                    </div>
                                  )}
                                </div>
                              );
                            })}
                        </div>
                      </div>
                    ) : (
                      /* Placeholder Gallery */
                      <div className="bg-background/95 backdrop-blur-sm border border-border rounded-xl overflow-hidden shadow-xl opacity-60">
                        <div className="p-4 pb-2">
                          <h4 className="text-sm font-semibold text-muted-foreground mb-3 flex items-center">
                            <Target className="w-4 h-4 mr-2" />
                            Workplace Gallery
                          </h4>
                        </div>

                        <div className="relative h-64 bg-gradient-to-br from-secondary/20 to-muted/30 flex items-center justify-center">
                          <div className="text-center space-y-2">
                            <div className="w-16 h-16 rounded-full bg-muted/50 flex items-center justify-center mx-auto">
                              <Target className="w-8 h-8 text-muted-foreground/50" />
                            </div>
                            <p className="text-sm text-muted-foreground/70">
                              Workplace photos coming soon
                            </p>
                          </div>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        {/* Enhanced Progress Indicator - Only show when in journey section */}
        {isInJourneySection && (
          <div className="fixed bottom-8 left-1/2 transform -translate-x-1/2 z-50">
            <div className="flex items-center space-x-3 bg-background/90 dark:bg-background/80 backdrop-blur-lg rounded-full px-6 py-3 border border-border shadow-2xl">
              <div className="w-3 h-3 rounded-full bg-gradient-to-r from-primary to-accent animate-pulse"></div>
              <span className="text-foreground font-medium text-sm">
                Journey Progress
              </span>
              <div className="w-32 h-3 bg-muted rounded-full overflow-hidden shadow-inner">
                <div
                  className="h-full bg-gradient-to-r from-primary via-accent to-primary transition-all duration-500 shadow-lg"
                  style={{ width: `${scrollProgress * 100}%` }}
                />
              </div>
              <span className="text-foreground font-bold text-sm min-w-[3rem] text-center">
                {Math.round(scrollProgress * 100)}%
              </span>
            </div>
          </div>
        )}
      </div>
    </section>
  );
}

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

