    const remotePath = `${UPLOAD_DIR}/${img}`;

    try {
      await bucket.upload(localPath, {
        destination: remotePath,
        metadata: {
          contentType: "image/png",
          cacheControl: "public, max-age=31536000"
        }
      });

      console.log(`â Uploaded: ${img}`);
    } catch (error) {
      console.error(`â Failed to upload ${img}:`, error);
    }
  }

  console.log("\nâ¨ Harley-Davidson images uploaded successfully!");
}

